# Определение класса
class Animal:
    def __init__(self, name):
        self.name = name

    def speak(self):
        pass

# Создание подклассов
class Dog(Animal):
    def speak(self):
        return f"{self.name} says Woof!"

class Cat(Animal):
    def speak(self):
        return f"{self.name} says Meow!"

# Создание объектов
dog = Dog("Buddy")
cat = Cat("Whiskers")

# Использование полиморфизма
animals = [dog, cat]
for animal in animals:
    print(animal.speak())

# Вывод:
# Buddy says Woof!
# Whiskers says Meow!

